// State transition: Init -> PreOp
HRESULT CTcComA_search::SetObjStateIP(ITComObjectServer* ipSrv, TComInitDataHdr* pInitData)
{
	HRESULT hr = S_OK;

	hr = SingleInstanceLock(ipSrv, GUID_NULL);
	if (SUCCEEDED(hr))
	{
		m_ContextInfo[0].OriginalTid = 0;
		m_ContextInfo[0].OriginalSampleTime = 10;

	}
	if (SUCCEEDED(hr))
	{
		PA_search_M = ::A_search();
		memset(&(AdditionalOutports.TcModuleOutput_TCModuleOutput),0,sizeof(AdditionalOutports.TcModuleOutput_TCModuleOutput));
		memset(&(AdditionalOutports.TcModuleOutput_TCModuleOutput1),0,sizeof(AdditionalOutports.TcModuleOutput_TCModuleOutput1));
		AdditionalInports.TcModuleInput_TCModuleInput1 = 0;
		AdditionalInports.TcModuleInput_TCModuleInput2 = 0;
		AdditionalInports.TcModuleInput_TCModuleInput3 = 0;
		AdditionalInports.TcModuleInput_TCModuleInput4 = 0;
		AdditionalInports.TcModuleInput_TCModuleInput5 = 0;
		AdditionalInports.TcModuleInput_TCModuleInput6 = 0;

	}
	if (FAILED(hr))
	{
		SetObjStatePI();
	}
	return hr;
}

// State transition: PreOp -> SafeOp
HRESULT CTcComA_search::SetObjStatePS(TComInitDataHdr* pInitData)
{
	HRESULT hr = S_OK;


	if(SUCCEEDED(hr))
	{
		auto fpState = FpControl(FpCtrlSection::Init);
		::A_search_initialize(PA_search_M);
		PA_search_M->dwork->TCModuleInput1_PWORK = reinterpret_cast<PVOID>(&(AdditionalInports.TcModuleInput_TCModuleInput1));
		PA_search_M->dwork->TCModuleInput2_PWORK = reinterpret_cast<PVOID>(&(AdditionalInports.TcModuleInput_TCModuleInput2));
		PA_search_M->dwork->TCModuleInput3_PWORK = reinterpret_cast<PVOID>(&(AdditionalInports.TcModuleInput_TCModuleInput3));
		PA_search_M->dwork->TCModuleInput4_PWORK = reinterpret_cast<PVOID>(&(AdditionalInports.TcModuleInput_TCModuleInput4));
		PA_search_M->dwork->TCModuleInput5_PWORK = reinterpret_cast<PVOID>(&(AdditionalInports.TcModuleInput_TCModuleInput5));
		PA_search_M->dwork->TCModuleInput6_PWORK = reinterpret_cast<PVOID>(&(AdditionalInports.TcModuleInput_TCModuleInput6));
		PA_search_M->dwork->TCModuleOutput_PWORK = reinterpret_cast<PVOID>(&(AdditionalOutports.TcModuleOutput_TCModuleOutput));
		PA_search_M->dwork->TCModuleOutput1_PWORK = reinterpret_cast<PVOID>(&(AdditionalOutports.TcModuleOutput_TCModuleOutput1));

		if (SUCCEEDED(hr))
			hr = CheckAndAdaptCycleTimes();
		m_Initialized = SUCCEEDED(hr);
		FpRestore(fpState);
	}

	if (FAILED(hr))
	{
		SetObjStateSP();
	}
	return hr;
}

